  ngOnInit() {
    this.userRole = this.authService.getUserRole();
    this.loadUserData();
    this.loadDashboardData();
  }
  loadUserData() {
    this.userName = localStorage.getItem('userName') || 'User';
    this.userEmail = localStorage.getItem('userEmail') || '';
    this.userAvatar = localStorage.getItem('userAvatar') || '/assets/default-avatar.png';
  }
  loadDashboardData() {
    this.dashboardApi.getDashboardData().subscribe({
      next: data => {
        console.log('Dashboard data:', data);
        if (data.stats) {
          this.studentStats = {
            coursesEnrolled: data.stats.coursesEnrolled || 0,
            coursesCompleted: data.stats.coursesCompleted || 0,
            hoursLearned: data.stats.hoursLearned || 0,
            certificates: data.stats.certificates || 0
          };
          this.instructorStats = {
            coursesCreated: data.stats.coursesCreated || 0,
            studentsCount: data.stats.studentsCount || 0,
            hoursTeaching: data.stats.hoursTeaching || 0,
            avgRating: data.stats.avgRating || 0
          };
        }
        if (data.enrolledCourses) {
          this.currentCourses = data.enrolledCourses.map(course => ({
            title: course.title,
            instructor: course.instructor,
            image: course.image || `https://picsum.photos/seed/${course.id}/60/60`,
            progress: course.progress || 0,
            students: course.students || 0,
            rating: course.rating || 0
          }));
        }
        if (data.recentActivities) {
          this.recentActivities = data.recentActivities.map(activity => ({
            icon: this.getActivityIcon(activity.type),
            type: activity.type?.toLowerCase() || 'info',
            description: activity.description,
            time: this.formatTime(activity.createdAt)
          }));
        }
        if (data.achievements) {
          this.recentAchievements = data.achievements.map(achievement => ({
            icon: achievement.icon || 'üèÜ',
            title: achievement.title,
            description: achievement.description
          }));
        }
      },
      error: error => {
        console.error('Error loading dashboard data:', error);
        // Keep default static data if API fails
      }
    });
    // Load user profile
    this.dashboardApi.getUserProfile().subscribe({
      next: profile => {
        if (profile) {
          this.userAvatar = profile.avatar || `https://i.pravatar.cc/150?u=${this.userEmail}`;
        }
      },
      error: error => {
        console.error('Error loading profile:', error);
      }
    });
  }
  getActivityIcon(type) {
    switch (type?.toLowerCase()) {
      case 'enroll':
        return 'üìö';
      case 'complete':
        return '‚úÖ';
      case 'achievement':
        return 'üèÜ';
      case 'quiz':
        return 'üéØ';
      default:
        return 'üìù';
    }
  }
  formatTime(dateString) {
    if (!dateString) return 'Recently';
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffDays = Math.floor(diffHours / 24);
    if (diffHours < 1) return 'Just now';
    if (diffHours < 24) return `${diffHours} hours ago`;
    if (diffDays < 7) return `${diffDays} days ago`;
    return date.toLocaleDateString();
  }
  getTimeOfDay() {
    const hour = new Date().getHours();
    if (hour < 12) return 'morning';
    if (hour < 17) return 'afternoon';
    return 'evening';
  }
  logout() {
    this.authService.logout();
    this.router.navigate(['/auth/login']);
  }
