import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClient, HttpClientModule } from '@angular/common/http';
import { Router } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { AuthService } from '../auth/services/auth.service';

@Component({
  selector: 'app-quiz',
  standalone: true,
  imports: [CommonModule, HttpClientModule, FormsModule],
  template: `
    <div class="quiz-container">
      <!-- Student View -->
      <div *ngIf="userRole === 'STUDENT'" class="student-view">
        <div class="quiz-header">
          <h1>ðŸ§  Practice Quizzes</h1>
          <p>Test your knowledge and improve your skills</p>
        </div>

        <div class="quiz-stats">
          <div class="stat-card">
            <div class="stat-icon">ðŸ“</div>
            <div class="stat-content">
              <h3>{{quizStats.total}}</h3>
              <p>Total Quizzes</p>
            </div>
          </div>
          <div class="stat-card">
            <div class="stat-icon">âœ…</div>
            <div class="stat-content">
              <h3>{{quizStats.completed}}</h3>
              <p>Completed</p>
            </div>
          </div>
          <div class="stat-card">
            <div class="stat-icon">ðŸŽ¯</div>
            <div class="stat-content">
              <h3>{{quizStats.averageScore}}%</h3>
              <p>Average Score</p>
            </div>
          </div>
          <div class="stat-card">
            <div class="stat-icon">ðŸ†</div>
            <div class="stat-content">
              <h3>{{quizStats.bestScore}}%</h3>
              <p>Best Score</p>
            </div>
          </div>
        </div>

        <div class="quiz-filters">
          <button 
            *ngFor="let category of categories" 
            class="filter-btn"
            [class.active]="selectedCategory === category"
            (click)="filterByCategory(category)">
            {{category}}
          </button>
        </div>

        <div class="quiz-grid">
          <div class="quiz-card" *ngFor="let quiz of filteredQuizzes">
            <div class="quiz-header-card">
              <div class="quiz-icon">{{quiz.icon}}</div>
              <div class="quiz-status" [class]="quiz.status">{{quiz.status}}</div>
            </div>
            <h3>{{quiz.title}}</h3>
            <p>{{quiz.description}}</p>
            <div class="quiz-info">
              <span class="info-item">
                <span class="info-icon">â“</span>
                {{quiz.questions}} Questions
              </span>
              <span class="info-item">
                <span class="info-icon">â±ï¸</span>
                {{quiz.timeLimit}} min
              </span>
              <span class="difficulty" [class]="quiz.difficulty">{{quiz.difficulty}}</span>
            </div>
            <div class="quiz-progress" *ngIf="quiz.progress > 0">
              <div class="progress-bar">
                <div class="progress-fill" [style.width.%]="quiz.progress"></div>
              </div>
              <span class="progress-text">{{quiz.progress}}% Complete</span>
            </div>
            <div class="quiz-actions">
              <button 
                class="action-btn primary" 
                (click)="startQuiz(quiz)"
                [disabled]="quiz.status === 'locked'">
                {{getButtonText(quiz)}}
              </button>
              <button 
                *ngIf="quiz.status === 'completed'" 
                class="action-btn secondary"
                (click)="viewResults(quiz)">
                View Results
              </button>
            </div>
          </div>
        </div>
      </div>

      <!-- Instructor View -->
      <div *ngIf="userRole === 'INSTRUCTOR'" class="instructor-view">
        <div class="quiz-header">
          <h1>ðŸ‘¨ðŸ« Quiz Management</h1>
          <p>Create and assign quizzes to students</p>
        </div>

        <div class="instructor-actions">
          <button class="action-btn primary" (click)="showCreateQuiz = true">âž• Create New Quiz</button>
          <button class="action-btn secondary" (click)="showStudentList = !showStudentList">ðŸ‘¥ View Students</button>
        </div>

        <!-- Student List -->
        <div class="student-list-card" *ngIf="showStudentList">
          <h3>ðŸ“‹ Student List</h3>
          <div class="students-grid">
            <div class="student-item" *ngFor="let student of students">
              <div class="student-info">
                <div class="student-avatar">{{getInitials(student.name)}}</div>
                <div class="student-details">
                  <h4>{{student.name}}</h4>
                  <p>ID: {{student.id}}</p>
                  <span class="student-email">{{student.email}}</span>
                </div>
              </div>
              <div class="student-stats">
                <span class="stat">ðŸ“š {{student.coursesCompleted}} courses</span>
                <span class="stat">ðŸŽ¯ {{student.averageScore}}% avg</span>
              </div>
            </div>
          </div>
        </div>

        <!-- Quiz Assignment -->
        <div class="quiz-assignment-card">
          <h3>ðŸŽ¯ Assign Quiz to Students</h3>
          <div class="assignment-form">
            <div class="form-group">
              <label>Select Quiz:</label>
              <select [(ngModel)]="selectedQuizId" class="form-select">
                <option value="">Choose a quiz...</option>
                <option *ngFor="let quiz of instructorQuizzes" [value]="quiz.id">{{quiz.title}}</option>
              </select>
            </div>

            <div class="form-group">
              <label>Select Students:</label>
              <div class="student-selection">
                <div class="select-all">
                  <input type="checkbox" (change)="toggleAllStudents($event)" id="selectAll">
                  <label for="selectAll">Select All Students</label>
                </div>
                <div class="student-checkboxes">
                  <div class="student-checkbox" *ngFor="let student of students">
                    <input 
                      type="checkbox" 
                      [id]="'student-' + student.id"
                      [(ngModel)]="student.selected"
                      (change)="updateSelectedStudents()">
                    <label [for]="'student-' + student.id">
                      {{student.name}} (ID: {{student.id}})
                    </label>
                  </div>
                </div>
              </div>
            </div>

            <div class="form-group">
              <label>Due Date:</label>
              <input type="datetime-local" [(ngModel)]="dueDate" class="form-input">
            </div>

            <button 
              class="action-btn primary" 
              (click)="assignQuiz()"
              [disabled]="!selectedQuizId || selectedStudentIds.length === 0">
              ðŸ“¤ Assign Quiz ({{selectedStudentIds.length}} students)
            </button>
          </div>
        </div>

        <!-- Instructor's Quiz List -->
        <div class="instructor-quizzes">
          <h3>ðŸ“ Your Quizzes</h3>
          <div class="quiz-grid">
            <div class="quiz-card instructor" *ngFor="let quiz of instructorQuizzes">
              <div class="quiz-header-card">
                <div class="quiz-icon">{{quiz.icon}}</div>
                <div class="quiz-actions-menu">
                  <button class="menu-btn" (click)="toggleQuizMenu(quiz.id)">â‹®</button>
                  <div class="menu-dropdown" *ngIf="quiz.showMenu">
                    <button (click)="editQuiz(quiz)">âœï¸ Edit</button>
                    <button (click)="viewQuizResults(quiz)">ðŸ“Š Results</button>
                    <button (click)="deleteQuiz(quiz)">ðŸ—‘ï¸ Delete</button>
                  </div>
                </div>
              </div>
              <h3>{{quiz.title}}</h3>
              <p>{{quiz.description}}</p>
              <div class="quiz-stats-instructor">
                <span class="stat">ðŸ‘¥ {{quiz.assignedTo}} assigned</span>
                <span class="stat">âœ… {{quiz.completed}} completed</span>
                <span class="stat">ðŸ“Š {{quiz.averageScore}}% avg score</span>
              </div>
              <div class="quiz-actions">
                <button class="action-btn secondary" (click)="assignExistingQuiz(quiz)">ðŸ“¤ Assign</button>
                <button class="action-btn primary" (click)="viewQuizResults(quiz)">ðŸ“Š View Results</button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <!-- Quiz Taking Interface -->
      <div class="quiz-modal" *ngIf="currentQuiz" (click)="closeQuiz($event)">
        <div class="quiz-content" (click)="$event.stopPropagation()">
          <div class="quiz-modal-header">
            <h2>{{currentQuiz.title}}</h2>
            <div class="quiz-timer">â±ï¸ {{formatTime(timeRemaining)}}</div>
            <button class="close-btn" (click)="closeQuiz()">âœ•</button>
          </div>

          <div class="question-progress">
            <div class="progress-bar">
              <div class="progress-fill" [style.width.%]="(currentQuestionIndex + 1) / currentQuiz.questions.length * 100"></div>
            </div>
            <span>Question {{currentQuestionIndex + 1}} of {{currentQuiz.questions.length}}</span>
          </div>

          <div class="question-section" *ngIf="currentQuestion">
            <h3>{{currentQuestion.question}}</h3>
            <div class="options">
              <button 
                *ngFor="let option of currentQuestion.options; let i = index"
                class="option-btn"
                [class.selected]="selectedAnswer === i"
                (click)="selectAnswer(i)">
                {{option}}
              </button>
            </div>
          </div>

          <div class="quiz-navigation">
            <button 
              class="nav-btn" 
              (click)="previousQuestion()"
              [disabled]="currentQuestionIndex === 0">
              Previous
            </button>
            <button 
              class="nav-btn primary" 
              (click)="nextQuestion()">
              {{currentQuestionIndex === currentQuiz.questions.length - 1 ? 'Finish' : 'Next'}}
            </button>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .quiz-container {
      padding: 40px;
      background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
      min-height: 100vh;
    }

    .quiz-header {
      text-align: center;
      margin-bottom: 40px;
    }

    .quiz-header h1 {
      font-size: 2.5rem;
      font-weight: 700;
      color: #1e293b;
      margin-bottom: 8px;
    }

    .quiz-header p {
      color: #64748b;
      font-size: 1.1rem;
    }

    .instructor-actions {
      display: flex;
      gap: 16px;
      margin-bottom: 30px;
    }

    .student-list-card, .quiz-assignment-card {
      background: white;
      border-radius: 16px;
      padding: 24px;
      margin-bottom: 30px;
      box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
    }

    .students-grid {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
      gap: 16px;
    }

    .student-item {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 16px;
      background: #f8fafc;
      border-radius: 12px;
      border: 1px solid #e2e8f0;
    }

    .student-info {
      display: flex;
      align-items: center;
