import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-quiz',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="quiz-container">
      <div class="quiz-header">
        <h1>Practice Quizzes</h1>
        <p>Test your knowledge and improve your skills</p>
      </div>

      <div class="quiz-stats">
        <div class="stat-card">
          <h3>{{quizStats.total}}</h3>
          <p>Total Quizzes</p>
        </div>
        <div class="stat-card">
          <h3>{{quizStats.completed}}</h3>
          <p>Completed</p>
        </div>
        <div class="stat-card">
          <h3>{{quizStats.averageScore}}%</h3>
          <p>Average Score</p>
        </div>
      </div>

      <div class="quiz-grid">
        <div class="quiz-card" *ngFor="let quiz of quizzes">
          <div class="quiz-icon">{{quiz.icon}}</div>
          <h3>{{quiz.title}}</h3>
          <p>{{quiz.description}}</p>
          <div class="quiz-info">
            <span>{{quiz.questions}} Questions</span>
            <span>{{quiz.timeLimit}} min</span>
            <span class="difficulty" [class]="quiz.difficulty">{{quiz.difficulty}}</span>
          </div>
          <button class="start-btn" (click)="startQuiz(quiz)">Start Quiz</button>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .quiz-container {
      padding: 40px;
      max-width: 1200px;
      margin: 0 auto;
      background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
      min-height: 100vh;
    }

    .quiz-header {
      text-align: center;
      margin-bottom: 40px;
    }

    .quiz-header h1 {
      font-size: 2.5rem;
      font-weight: 700;
      color: #1e293b;
      margin-bottom: 8px;
    }

    .quiz-header p {
      color: #64748b;
      font-size: 1.1rem;
    }

    .quiz-stats {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
      gap: 24px;
      margin-bottom: 40px;
    }

    .stat-card {
      background: white;
      padding: 24px;
      border-radius: 16px;
      text-align: center;
      box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
    }

    .stat-card h3 {
      font-size: 2rem;
      font-weight: 700;
      color: #3b82f6;
      margin-bottom: 8px;
    }

    .stat-card p {
      color: #64748b;
      font-weight: 500;
    }

    .quiz-grid {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
      gap: 24px;
    }

    .quiz-card {
      background: white;
      padding: 24px;
      border-radius: 16px;
      box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
      transition: all 0.3s ease;
      text-align: center;
    }

    .quiz-card:hover {
      transform: translateY(-4px);
      box-shadow: 0 16px 50px rgba(0, 0, 0, 0.15);
    }

    .quiz-icon {
      font-size: 3rem;
      margin-bottom: 16px;
    }

    .quiz-card h3 {
      font-size: 1.25rem;
      font-weight: 600;
      color: #1e293b;
      margin-bottom: 8px;
    }

    .quiz-card p {
      color: #64748b;
      margin-bottom: 16px;
    }

    .quiz-info {
      display: flex;
      justify-content: space-between;
      margin-bottom: 20px;
      font-size: 0.875rem;
      color: #64748b;
    }

    .difficulty {
      padding: 4px 8px;
      border-radius: 8px;
      font-weight: 600;
    }

    .difficulty.easy {
      background: #dcfce7;
      color: #166534;
    }

    .difficulty.medium {
      background: #fef3c7;
      color: #92400e;
    }

    .difficulty.hard {
      background: #fecaca;
      color: #991b1b;
    }

    .start-btn {
      width: 100%;
      padding: 12px 20px;
      background: linear-gradient(135deg, #3b82f6, #1e40af);
      color: white;
      border: none;
      border-radius: 12px;
      font-weight: 600;
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .start-btn:hover {
      transform: translateY(-2px);
      box-shadow: 0 8px 25px rgba(59, 130, 246, 0.3);
    }
  `]
})
export class QuizComponent implements OnInit {
  quizzes = [
    {
      title: 'JavaScript Fundamentals',
      description: 'Test your basic JavaScript knowledge',
      questions: 15,
      timeLimit: 20,
      difficulty: 'easy',
      icon: 'üìù'
    },
    {
      title: 'React Components',
      description: 'Challenge your React skills',
      questions: 12,
      timeLimit: 15,
      difficulty: 'medium',
      icon: '‚öõÔ∏è'
    },
    {
      title: 'Node.js Advanced',
      description: 'Advanced server-side concepts',
      questions: 18,
      timeLimit: 25,
      difficulty: 'hard',
      icon: 'üöÄ'
    },
    {
      title: 'CSS Layouts',
      description: 'Master CSS Grid and Flexbox',
      questions: 10,
      timeLimit: 12,
      difficulty: 'easy',
      icon: 'üé®'
    }
  ];

  quizStats = {
    total: 4,
    completed: 2,
    averageScore: 85
  };

  ngOnInit() {}

  startQuiz(quiz: any) {
    alert(`Starting ${quiz.title} quiz!`);
  }
}
