import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

@Component({
  selector: 'app-quiz',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="quiz-page">
      <div class="page-header">
        <h1>Practice Quizzes</h1>
        <p>Test your knowledge and track your progress</p>
      </div>

      <div class="quiz-stats">
        <div class="stat-card">
          <h3>Total Quizzes</h3>
          <div class="stat-number">{{quizStats.total}}</div>
        </div>
        <div class="stat-card">
          <h3>Completed</h3>
          <div class="stat-number">{{quizStats.completed}}</div>
        </div>
        <div class="stat-card">
          <h3>Average Score</h3>
          <div class="stat-number">{{quizStats.averageScore}}%</div>
        </div>
        <div class="stat-card">
          <h3>Best Score</h3>
          <div class="stat-number">{{quizStats.bestScore}}%</div>
        </div>
      </div>

      <div class="quiz-grid">
        <div class="quiz-card" *ngFor="let quiz of quizzes" [class]="quiz.status">
          <div class="quiz-header">
            <div class="quiz-icon">{{quiz.icon}}</div>
            <div class="quiz-difficulty" [class]="quiz.difficulty">{{quiz.difficulty}}</div>
          </div>
          <h3>{{quiz.title}}</h3>
          <p>{{quiz.description}}</p>
          <div class="quiz-details">
            <div class="detail">
              <span class="label">Questions</span>
              <span class="value">{{quiz.questions}}</span>
            </div>
            <div class="detail">
              <span class="label">Time</span>
              <span class="value">{{quiz.timeLimit}} min</span>
            </div>
            <div class="detail">
              <span class="label">Best Score</span>
              <span class="value">{{quiz.bestScore || '--'}}%</span>
            </div>
          </div>
          <button class="btn-primary" *ngIf="quiz.status === 'available'" (click)="startQuiz(quiz)">Start Quiz</button>
          <button class="btn-secondary" *ngIf="quiz.status === 'completed'">Retake Quiz</button>
          <button class="btn-success" *ngIf="quiz.status === 'mastered'">Mastered âœ“</button>
        </div>
      </div>

      <div class="recent-attempts" *ngIf="recentAttempts.length > 0">
        <h2>Recent Quiz Attempts</h2>
        <div class="attempts-list">
          <div class="attempt-item" *ngFor="let attempt of recentAttempts">
            <div class="attempt-info">
              <h4>{{attempt.quizTitle}}</h4>
              <p>Score: {{attempt.score}}% â€¢ {{attempt.date | date:'short'}}</p>
            </div>
            <div class="attempt-score" [class]="getScoreClass(attempt.score)">
              {{attempt.score}}%
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .quiz-page { padding: 24px; max-width: 1200px; margin: 0 auto; }
    .page-header { text-align: center; margin-bottom: 40px; }
    .page-header h1 { font-size: 36px; font-weight: 700; color: #1e293b; margin-bottom: 8px; }
    .page-header p { color: #64748b; font-size: 18px; }
    .quiz-stats { display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 20px; margin-bottom: 40px; }
    .stat-card { background: white; padding: 24px; border-radius: 16px; text-align: center; box-shadow: 0 4px 20px rgba(0,0,0,0.1); }
    .stat-card h3 { font-size: 14px; color: #64748b; margin-bottom: 8px; }
    .stat-number { font-size: 32px; font-weight: 700; color: #1e293b; }
    .quiz-grid { display: grid; grid-template-columns: repeat(auto-fill, minmax(320px, 1fr)); gap: 24px; margin-bottom: 40px; }
    .quiz-card { background: white; border-radius: 16px; padding: 28px; box-shadow: 0 8px 32px rgba(0,0,0,0.1); transition: all 0.3s ease; }
    .quiz-card:hover { transform: translateY(-6px); box-shadow: 0 16px 50px rgba(0,0,0,0.2); }
