import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClient, HttpClientModule } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-quiz',
  standalone: true,
  imports: [CommonModule, HttpClientModule],
  template: `
    <div class="quiz-container">
      <div class="quiz-header">
        <h1>üß† Practice Quizzes</h1>
        <p>Test your knowledge and improve your skills</p>
      </div>

      <div class="quiz-stats">
        <div class="stat-card">
          <div class="stat-icon">üìù</div>
          <div class="stat-content">
            <h3>{{quizStats.total}}</h3>
            <p>Total Quizzes</p>
          </div>
        </div>
        <div class="stat-card">
          <div class="stat-icon">‚úÖ</div>
          <div class="stat-content">
            <h3>{{quizStats.completed}}</h3>
            <p>Completed</p>
          </div>
        </div>
        <div class="stat-card">
          <div class="stat-icon">üéØ</div>
          <div class="stat-content">
            <h3>{{quizStats.averageScore}}%</h3>
            <p>Average Score</p>
          </div>
        </div>
        <div class="stat-card">
          <div class="stat-icon">üèÜ</div>
          <div class="stat-content">
            <h3>{{quizStats.bestScore}}%</h3>
            <p>Best Score</p>
          </div>
        </div>
      </div>

      <div class="quiz-filters">
        <button 
          *ngFor="let category of categories" 
          class="filter-btn"
          [class.active]="selectedCategory === category"
          (click)="filterByCategory(category)">
          {{category}}
        </button>
      </div>

      <div class="quiz-grid">
        <div class="quiz-card" *ngFor="let quiz of filteredQuizzes">
          <div class="quiz-header-card">
            <div class="quiz-icon">{{quiz.icon}}</div>
            <div class="quiz-status" [class]="quiz.status">{{quiz.status}}</div>
          </div>
          <h3>{{quiz.title}}</h3>
          <p>{{quiz.description}}</p>
          <div class="quiz-info">
            <span class="info-item">
              <span class="info-icon">‚ùì</span>
              {{quiz.questions}} Questions
            </span>
            <span class="info-item">
              <span class="info-icon">‚è±Ô∏è</span>
              {{quiz.timeLimit}} min
            </span>
            <span class="difficulty" [class]="quiz.difficulty">{{quiz.difficulty}}</span>
          </div>
          <div class="quiz-progress" *ngIf="quiz.progress > 0">
            <div class="progress-bar">
              <div class="progress-fill" [style.width.%]="quiz.progress"></div>
            </div>
            <span class="progress-text">{{quiz.progress}}% Complete</span>
          </div>
          <div class="quiz-actions">
            <button 
              class="action-btn primary" 
              (click)="startQuiz(quiz)"
              [disabled]="quiz.status === 'locked'">
              {{getButtonText(quiz)}}
            </button>
            <button 
              *ngIf="quiz.status === 'completed'" 
              class="action-btn secondary"
              (click)="viewResults(quiz)">
              View Results
            </button>
          </div>
        </div>
      </div>

      <!-- Quiz Taking Interface -->
      <div class="quiz-modal" *ngIf="currentQuiz" (click)="closeQuiz($event)">
        <div class="quiz-content" (click)="$event.stopPropagation()">
          <div class="quiz-modal-header">
            <h2>{{currentQuiz.title}}</h2>
            <div class="quiz-timer">‚è±Ô∏è {{formatTime(timeRemaining)}}</div>
            <button class="close-btn" (click)="closeQuiz()">‚úï</button>
          </div>

          <div class="question-progress">
            <div class="progress-bar">
              <div class="progress-fill" [style.width.%]="(currentQuestionIndex + 1) / currentQuiz.questions.length * 100"></div>
            </div>
            <span>Question {{currentQuestionIndex + 1}} of {{currentQuiz.questions.length}}</span>
          </div>

          <div class="question-section" *ngIf="currentQuestion">
            <h3>{{currentQuestion.question}}</h3>
            <div class="options">
              <button 
                *ngFor="let option of currentQuestion.options; let i = index"
                class="option-btn"
                [class.selected]="selectedAnswer === i"
                (click)="selectAnswer(i)">
                {{option}}
              </button>
            </div>
          </div>

          <div class="quiz-navigation">
            <button 
              class="nav-btn" 
              (click)="previousQuestion()"
              [disabled]="currentQuestionIndex === 0">
              Previous
            </button>
            <button 
              class="nav-btn primary" 
              (click)="nextQuestion()">
              {{currentQuestionIndex === currentQuiz.questions.length - 1 ? 'Finish' : 'Next'}}
            </button>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .quiz-container {
      padding: 40px;
      background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
      min-height: 100vh;
    }

    .quiz-header {
      text-align: center;
      margin-bottom: 40px;
    }

    .quiz-header h1 {
      font-size: 2.5rem;
      font-weight: 700;
      color: #1e293b;
      margin-bottom: 8px;
    }

    .quiz-header p {
      color: #64748b;
      font-size: 1.1rem;
    }

    .quiz-stats {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
      gap: 24px;
      margin-bottom: 40px;
    }

    .stat-card {
      background: white;
      padding: 24px;
      border-radius: 16px;
      display: flex;
      align-items: center;
      gap: 16px;
      box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
      transition: all 0.3s ease;
    }

    .stat-card:hover {
      transform: translateY(-4px);
    }

    .stat-icon {
      font-size: 2rem;
    }

    .stat-content h3 {
      font-size: 2rem;
      font-weight: 700;
      color: #3b82f6;
      margin-bottom: 4px;
    }

    .stat-content p {
      color: #64748b;
      font-weight: 500;
    }

    .quiz-filters {
      display: flex;
      gap: 12px;
      margin-bottom: 30px;
      flex-wrap: wrap;
    }

    .filter-btn {
      padding: 8px 16px;
      border: 2px solid #e2e8f0;
      background: white;
      border-radius: 20px;
      cursor: pointer;
      transition: all 0.3s ease;
      font-weight: 500;
    }

    .filter-btn.active {
      background: #3b82f6;
      color: white;
      border-color: #3b82f6;
    }

    .quiz-grid {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
      gap: 24px;
    }

    .quiz-card {
      background: white;
      padding: 24px;
      border-radius: 16px;
      box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
      transition: all 0.3s ease;
    }

    .quiz-card:hover {
      transform: translateY(-4px);
      box-shadow: 0 16px 50px rgba(0, 0, 0, 0.15);
    }

    .quiz-header-card {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 16px;
    }

    .quiz-icon {
      font-size: 2.5rem;
    }

    .quiz-status {
      padding: 4px 12px;
      border-radius: 12px;
      font-size: 0.75rem;
      font-weight: 600;
      text-transform: uppercase;
    }

    .quiz-status.available {
      background: #dcfce7;
      color: #166534;
    }

    .quiz-status.in-progress {
      background: #fef3c7;
      color: #92400e;
    }

    .quiz-status.completed {
      background: #dbeafe;
      color: #1e40af;
    }

    .quiz-status.locked {
      background: #f3f4f6;
      color: #6b7280;
    }

    .quiz-card h3 {
      font-size: 1.25rem;
      font-weight: 600;
      color: #1e293b;
      margin-bottom: 8px;
    }

    .quiz-card p {
      color: #64748b;
      margin-bottom: 16px;
    }

    .quiz-info {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 16px;
      font-size: 0.875rem;
      color: #64748b;
      flex-wrap: wrap;
      gap: 8px;
    }

    .info-item {
      display: flex;
      align-items: center;
      gap: 4px;
    }

    .info-icon {
      font-size: 0.75rem;
    }

    .difficulty {
      padding: 4px 8px;
      border-radius: 8px;
      font-weight: 600;
      font-size: 0.75rem;
    }

    .difficulty.easy {
      background: #dcfce7;
      color: #166534;
    }

    .difficulty.medium {
      background: #fef3c7;
      color: #92400e;
    }

    .difficulty.hard {
      background: #fecaca;
      color: #991b1b;
    }

    .quiz-progress {
      margin-bottom: 16px;
    }

    .progress-bar {
      width: 100%;
      height: 6px;
      background: #e2e8f0;
      border-radius: 3px;
      overflow: hidden;
      margin-bottom: 8px;
    }

    .progress-fill {
      height: 100%;
      background: linear-gradient(135deg, #3b82f6, #1d4ed8);
      transition: width 0.3s ease;
    }

    .progress-text {
      font-size: 0.875rem;
      color: #64748b;
    }

    .quiz-actions {
      display: flex;
      gap: 12px;
    }

    .action-btn {
      flex: 1;
      padding: 12px 20px;
      border: none;
      border-radius: 12px;
      font-weight: 600;
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .action-btn.primary {
      background: linear-gradient(135deg, #3b82f6, #1e40af);
      color: white;
    }

    .action-btn.secondary {
      background: #f8fafc;
      color: #64748b;
      border: 2px solid #e2e8f0;
    }

    .action-btn:hover:not(:disabled) {
      transform: translateY(-2px);
      box-shadow: 0 8px 25px rgba(59, 130, 246, 0.3);
    }

    .action-btn:disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    /* Quiz Modal Styles */
    .quiz-modal {
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: rgba(0, 0, 0, 0.8);
      display: flex;
      align-items: center;
      justify-content: center;
      z-index: 1000;
    }

    .quiz-content {
      background: white;
      border-radius: 16px;
      padding: 32px;
      max-width: 800px;
      width: 90%;
      max-height: 90%;
      overflow-y: auto;
    }

    .quiz-modal-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 24px;
    }

    .quiz-modal-header h2 {
      color: #1e293b;
      margin: 0;
    }

    .quiz-timer {
      font-weight: 600;
      color: #dc2626;
    }

    .close-btn {
      background: none;
      border: none;
      font-size: 1.5rem;
      cursor: pointer;
      color: #64748b;
    }

    .question-progress {
      margin-bottom: 32px;
    }

    .question-progress span {
      font-size: 0.875rem;
      color: #64748b;
      margin-top: 8px;
      display: block;
    }

    .question-section h3 {
      color: #1e293b;
      margin-bottom: 24px;
      font-size: 1.25rem;
    }

    .options {
      display: flex;
      flex-direction: column;
      gap: 12px;
      margin-bottom: 32px;
    }

    .option-btn {
      padding: 16px;
      border: 2px solid #e2e8f0;
      background: white;
      border-radius: 12px;
      text-align: left;
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .option-btn:hover {
      border-color: #3b82f6;
    }

    .option-btn.selected {
      border-color: #3b82f6;
      background: #eff6ff;
    }

    .quiz-navigation {
      display: flex;
      justify-content: space-between;
    }

    .nav-btn {
      padding: 12px 24px;
      border: 2px solid #e2e8f0;
      background: white;
      border-radius: 12px;
      cursor: pointer;
      font-weight: 600;
      transition: all 0.3s ease;
    }

    .nav-btn.primary {
      background: #3b82f6;
      color: white;
      border-color: #3b82f6;
    }

    .nav-btn:disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }
  `]
})
export class QuizComponent implements OnInit {
  quizzes: any[] = [];
  filteredQuizzes: any[] = [];
  categories = ['All', 'JavaScript', 'React', 'Node.js', 'CSS', 'Python'];
  selectedCategory = 'All';

  currentQuiz: any = null;
  currentQuestionIndex = 0;
  currentQuestion: any = null;
  selectedAnswer: number | null = null;
  userAnswers: any[] = [];
  timeRemaining = 0;
  timer: any;

  quizStats = {
    total: 0,
    completed: 0,
    averageScore: 0,
    bestScore: 0
  };

  constructor(private http: HttpClient, private router: Router) {}

  ngOnInit() {
    this.loadQuizzes();
    this.loadQuizStats();
  }

  loadQuizzes() {
    // Sample quiz data - replace with API call
    this.quizzes = [
      {
        id: 1,
        title: 'JavaScript Fundamentals',
        description: 'Test your basic JavaScript knowledge',
        questions: [
          {
            question: 'What is the correct way to declare a variable in JavaScript?',
            options: ['var x = 5;', 'variable x = 5;', 'v x = 5;', 'declare x = 5;'],
