export class LoginComponent implements OnInit {
  authForm: FormGroup;
  selectedRole: string = '';
  isLogin: boolean = true;
  showPassword: boolean = false;
  isLoading: boolean = false;
  particles: any[] = [];
  showForgot: boolean = false;
  forgotEmail: string = '';
  resetToken: string = '';
  newPassword: string = '';

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router,
    private route: ActivatedRoute
  ) {
    this.authForm = this.fb.group({
      name: [''],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(8)]]
    });
  }

  ngOnInit() {
    this.route.queryParams.subscribe(params => {
      this.selectedRole = params['role'] || localStorage.getItem('selectedRole') || '';
      this.isLogin = params['mode'] !== 'register';

      // If coming from onboarding, set to register mode
      if (params['onboarded'] === 'true') {
        this.isLogin = false;
        this.authForm.get('name')?.setValidators([Validators.required]);
        this.authForm.get('name')?.updateValueAndValidity();

        // Pre-fill name from onboarding data
        const onboardingData = JSON.parse(localStorage.getItem('onboardingData') || '{}');
        if (onboardingData.name) {
          this.authForm.patchValue({ name: onboardingData.name });
        }
      }
    });
    this.generateParticles();
  }

  generateParticles() {
    for (let i = 0; i < 20; i++) {
      this.particles.push({
        x: Math.random() * 100,
        delay: Math.random() * 8
      });
    }
  }

  openForgotPassword(event: Event) {
    event.preventDefault();
    this.showForgot = true;
    this.resetToken = '';
    this.newPassword = '';
    this.forgotEmail = this.authForm.get('email')?.value || '';
  }

  closeForgot() {
    this.showForgot = false;
  }

  requestReset() {
    if (!this.forgotEmail) { alert('Enter your email'); return; }
    this.authService.forgotPassword(this.forgotEmail).subscribe({
      next: (resp) => {
        this.resetToken = resp.resetToken;
        alert('Reset token generated. In production, check your email.');
      },
      error: () => alert('Failed to request password reset')
    });
  }

  submitReset() {
    if (!this.resetToken || !this.newPassword) { alert('Enter token and new password'); return; }
    this.authService.resetPassword(this.resetToken, this.newPassword).subscribe({
      next: () => {
        alert('Password reset successfully. Please login with your new password.');
        this.showForgot = false;
      },
      error: () => alert('Failed to reset password')
    });
  }

  togglePassword() {
    this.showPassword = !this.showPassword;
  }

  toggleMode() {
    this.isLogin = !this.isLogin;
    if (!this.isLogin) {
      this.authForm.get('name')?.setValidators([Validators.required]);
      this.authForm.get('password')?.setValidators([Validators.required, Validators.minLength(8)]);
      // For student registration, redirect to onboarding first
      if (this.selectedRole === 'student') {
        this.router.navigate(['/onboarding'], { 
          queryParams: { 
            role: this.selectedRole,
            mode: 'register'
          }
        });
        return;
      }
    } else {
      this.authForm.get('name')?.clearValidators();
      this.authForm.get('password')?.setValidators([Validators.required]);
    }
    this.authForm.get('name')?.updateValueAndValidity();
    this.authForm.get('password')?.updateValueAndValidity();
  }

  onSubmit() {
    if (this.authForm.valid) {
      const formData = this.authForm.value;

      // Real authentication validation
      const validCredentials: {[key: string]: string} = {
        'student@test.com': 'password123',
        'instructor@test.com': 'password123', 
        'admin@test.com': 'password123',
        'manideep.gonugunta1802@gmail.com': '123456789'
      };

      // Use real backend authentication
      if (this.isLogin) {
        this.authService.login(formData).subscribe({
          next: (response) => {
            localStorage.setItem('token', response.token);
            localStorage.setItem('userId', response.userId);
            localStorage.setItem('userRole', response.role);
            localStorage.setItem('userEmail', response.email);
            localStorage.setItem('userName', response.name);
            this.redirectBasedOnRole(response.role);
          },
          error: (error) => {
            alert('Invalid credentials! Please check your email and password.');
          }
        });
      } else {
        const onboardingData = JSON.parse(localStorage.getItem('onboardingData') || '{}');
        const registerData = {
          ...formData,
          role: this.selectedRole.toUpperCase(),
          ...onboardingData // Include onboarding data in registration
        };
        this.authService.register(registerData).subscribe({
          next: (response) => {
            localStorage.setItem('token', response.token);
            localStorage.setItem('userId', response.userId);
            localStorage.setItem('userRole', response.role);
            localStorage.setItem('userEmail', response.email);
            localStorage.setItem('userName', response.name);
            // Clear onboarding data after successful registration
            localStorage.removeItem('onboardingData');
            this.redirectBasedOnRole(response.role);
          },
          error: (error) => {
            console.error('Registration error:', error);
            alert('Registration failed! Email might already exist.');
          }
        });
      }

    }
  }

  goBack() {
    this.router.navigate(['/']);
  }

  redirectBasedOnRole(role: string) {
    console.log('Redirecting to role:', role);
    const route = role === 'STUDENT' ? '/dashboard/student' : 
                  role === 'INSTRUCTOR' ? '/dashboard/instructor' : 
                  role === 'ADMIN' ? '/dashboard/admin' : '/dashboard/student';

    console.log('Navigating to:', route);
    this.router.navigateByUrl(route);
  }
}
