import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-profile',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="profile-container">
      <!-- Enhanced Header -->
      <div class="profile-header">
        <div class="header-background"></div>
        <div class="profile-content-header">
          <div class="profile-main-info">
            <div class="profile-avatar-placeholder">
              <span class="avatar-initials">{{getInitials()}}</span>
            </div>
            <div class="profile-details">
              <h1>{{userProfile.name}}</h1>
              <p class="profile-title">{{userProfile.title}}</p>
              <div class="profile-meta">
                <span class="meta-item">üìç {{userProfile.location || 'Location not set'}}</span>
                <span class="meta-item">üìÖ Joined {{userProfile.joinDate}}</span>
              </div>
            </div>
          </div>
          <div class="profile-actions">
            <button class="action-btn secondary">üì§ Share Profile</button>
            <button class="action-btn primary">‚úèÔ∏è Edit Profile</button>
          </div>
        </div>

        <!-- Stats Cards -->
        <div class="stats-cards">
          <div class="stat-card">
            <div class="stat-icon">üìö</div>
            <div class="stat-content">
              <span class="stat-number">{{userProfile.coursesCompleted}}</span>
              <span class="stat-label">Courses Completed</span>
            </div>
          </div>
          <div class="stat-card">
            <div class="stat-icon">‚è±Ô∏è</div>
            <div class="stat-content">
              <span class="stat-number">{{userProfile.hoursLearned}}</span>
              <span class="stat-label">Hours Learned</span>
            </div>
          </div>
          <div class="stat-card">
            <div class="stat-icon">üèÜ</div>
            <div class="stat-content">
              <span class="stat-number">{{userProfile.certificates}}</span>
              <span class="stat-label">Certificates</span>
            </div>
          </div>
          <div class="stat-card">
            <div class="stat-icon">üî•</div>
            <div class="stat-content">
              <span class="stat-number">{{userProfile.streak}}</span>
              <span class="stat-label">Day Streak</span>
            </div>
          </div>
        </div>
      </div>

      <!-- Profile Content -->
      <div class="profile-content">
        <!-- Left Column -->
        <div class="profile-left">
          <!-- About Section -->
          <div class="profile-card">
            <h3>üìù About</h3>
            <p>{{userProfile.bio}}</p>
          </div>

          <!-- Skills Section -->
          <div class="profile-card">
            <h3>üéØ Skills</h3>
            <div class="skills-grid">
              <div *ngFor="let skill of userProfile.skills" class="skill-tag">
                {{skill.name}}
                <div class="skill-level" [style.width.%]="skill.level"></div>
              </div>
            </div>
          </div>

          <!-- Achievements -->
          <div class="profile-card">
            <h3>üèÜ Achievements</h3>
            <div class="achievements-grid">
              <div *ngFor="let achievement of userProfile.achievements" class="achievement">
                <span class="achievement-icon">{{achievement.icon}}</span>
                <div class="achievement-info">
                  <h4>{{achievement.title}}</h4>
                  <p>{{achievement.description}}</p>
                </div>
              </div>
            </div>
          </div>
        </div>

        <!-- Right Column -->
        <div class="profile-right">
          <!-- Learning Activity -->
          <div class="profile-card">
            <h3>üìä Learning Activity</h3>
            <div class="activity-chart">
              <div class="activity-week">
                <div *ngFor="let day of activityData" class="activity-day">
                  <div class="activity-bar" [style.height.%]="day.activity"></div>
                  <span class="day-label">{{day.day}}</span>
                </div>
              </div>
            </div>
          </div>

          <!-- Recent Courses -->
          <div class="profile-card">
            <h3>üìö Recent Courses</h3>
            <div class="recent-courses">
              <div *ngFor="let course of recentCourses" class="course-item">
                <img [src]="course.image" alt="Course" class="course-thumb">
                <div class="course-info">
                  <h4>{{course.title}}</h4>
                  <div class="course-progress">
                    <div class="progress-bar">
                      <div class="progress-fill" [style.width.%]="course.progress"></div>
                    </div>
                    <span class="progress-text">{{course.progress}}%</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <!-- Certificates -->
          <div class="profile-card">
            <h3>üéì Certificates</h3>
            <div class="certificates-grid">
              <div *ngFor="let cert of certificates" class="certificate">
                <div class="cert-icon">üèÜ</div>
                <div class="cert-info">
                  <h4>{{cert.title}}</h4>
                  <p>{{cert.issuer}} ‚Ä¢ {{cert.date}}</p>
                </div>
                <button class="view-cert-btn">View</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .profile-container {
      padding: 40px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      min-height: 100vh;
    }

    .profile-header {
      position: relative;
      background: white;
      border-radius: 24px;
      margin-bottom: 30px;
      box-shadow: 0 20px 60px rgba(0, 0, 0, 0.1);
      overflow: hidden;
    }

    .header-background {
      height: 120px;
      background: linear-gradient(135deg, #3b82f6 0%, #1e40af 100%);
    }

    .profile-content-header {
      padding: 0 40px 30px;
      margin-top: -60px;
      position: relative;
      z-index: 2;
    }

    .profile-main-info {
      display: flex;
      align-items: end;
      gap: 24px;
      margin-bottom: 30px;
    }

    .profile-avatar-placeholder {
      width: 120px;
      height: 120px;
      background: linear-gradient(135deg, #667eea, #764ba2);
      border-radius: 20px;
